mapper.select:
select table_name
FROM USER_TAB_COLUMNS
WHERE column_name in (${inval}) and COLUMN_ID = ${size}

        select eno, *
		from(
				select ROWNUM AS eno, *
				from (select *
					  from #{tableName}
					  order by NOTICE_NO desc)
				where ROWNUM <= #{endRowNo})
		where eno >= #{startRowNo}
